("SELECT * FROM productos INNER JOIN productos_opciones ON productos.ID_Producto=productos_opciones.ID_Producto INNER JOIN opciones ON productos_opciones.ID_Opcion=opciones.ID_Opcion INNER JOIN secciones_opciones ON opciones.ID_Opcion=secciones_opciones.ID_Opcion INNER JOIN secciones ON secciones_opciones.ID_Seccion=secciones.ID_Seccion INNER JOIN tiendas_secciones ON secciones.ID_Seccion=tiendas_secciones.ID_Seccion INNER JOIN tiendas ON tiendas_secciones.ID_Tienda=tiendas.ID_Tienda WHERE tiendas.ID_Tienda = 255");

// MUESTRA TODOS LOS PRODUCTO QUE TIENE UNA SECCION DE UNA TIENDA
("SELECT productos.ID_Producto, producto, opcion FROM productos INNER JOIN productos_opciones ON productos.ID_Producto=productos_opciones.ID_Producto INNER JOIN opciones ON productos_opciones.ID_Opcion=opciones.ID_Opcion INNER JOIN secciones_opciones ON opciones.ID_Opcion=secciones_opciones.ID_Opcion INNER JOIN secciones ON secciones_opciones.ID_Seccion=secciones.ID_Seccion INNER JOIN tiendas_secciones ON secciones.ID_Seccion=tiendas_secciones.ID_Seccion INNER JOIN tiendas ON tiendas_secciones.ID_Tienda=tiendas.ID_Tienda WHERE tiendas.ID_Tienda = 225 AND secciones.ID_Seccion = 20 ");